 * 
 */
public class MyApplication extends Application {
	public static RequestQueue queue;

	@Override
	public void onCreate() {
		super.onCreate();
		queue = Volley.newRequestQueue(getApplicationContext());
	}

	/**
	 * @Title: getRequestQueue
	 * @Description: 
	 * @param @return 
	 * @return RequestQueue 
	 * @throws
